Add-Type -Path '.\MyLibrary2.dll'; $STD_OUTPUT_HANDLE = -11; $textToWrite = 'Printing text using the Win32 API WriteConsole, in a type (instead of using -passthru), in a DLL : This test worked correctly'; $handle = $consoleApi::GetStdHandle($STD_OUTPUT_HANDLE);$charsWritten = $null; if (-not $consoleApi::WriteConsole($handle, $textToWrite, $textToWrite.Length, [ref]$charsWritten, [IntPtr]::Zero)) { Write-Warning 'Failed to write to console!'; exit 2; }